import * as React from "react";
import { useState, useEffect, useRef } from "react";
import { useDispatch, useSelector } from 'react-redux';
import { getMoviesBySearch } from "../../api/movies";
import { getAllGenres } from "../../api/movies";
import { getMoviesByRating } from "../../api/movies";
import {debounce} from "lodash";

export default function Navbar() {

  const dispatch = useDispatch();

  const [isFixed, setIsFixed] = useState(false);
  const navBarRef = useRef(null);
  const logosRef = useRef(null);

  useEffect(() => {
    const handleScroll = () => {
      const navHeight = navBarRef.current.offsetHeight;
      setIsFixed(window.scrollY > navHeight);
    };

    window.addEventListener('scroll', handleScroll);

    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const handleLogos = () => {
    logosRef.current.style.display = isFixed ? 'inline' : 'none';
  };

  useEffect(() => {
    handleLogos();
  }, [isFixed]);

  const onSearchChange = debounce((e) => {
    dispatch(getMoviesBySearch(e.target.value))
  }, 500)

  return (
    <header ref={navBarRef} className={`nav-bar flex ${isFixed ? 'fixed toleft' : ''}`}>
      <h1 id="logo" ref={logosRef}> Movie Search app</h1>
        <div>
          < input type ="text" maxLength="40" onChange={onSearchChange}/>
          <button>Watchlist</button>
        </div>
        <div>
          <button>Watchlist</button>
        </div>
    </header>
  );
}
