import React, { useState,  useRef } from "react";
import { useDispatch, useSelector } from 'react-redux';
import { getMoviesByRating } from "../../../api/movies"
import { debounce } from "lodash";
import {
  Popover,
  PopoverTrigger,
  PopoverContent,
  PopoverHeader,
  PopoverBody,
  PopoverFooter,
  PopoverArrow,
  PopoverCloseButton,
  Button
} from '@chakra-ui/react'

export default function Popup() {

  const dispatch = useDispatch();
  const initialFocusRef = React.useRef()
  const { rating } = useSelector((state) => state.movies);
  const [btn1Value, setBtn1Value] = useState(null);
  const [btn2Value, setBtn2Value] = useState(null);
  const btn2Ref = useRef(null);

  const onRatingChange = (event) => {
    const target = event.target;

    if (target.className === 'btn1') {
      setBtn1Value(parseInt(target.value));
      target.disabled = true;
      btn2Ref.current.disabled = false;
    } else if (target.className === 'btn2') {
      setBtn2Value(parseFloat(target.value));
    }
  };

  //dispatch(getMoviesByRating(e.target.value));


  return (
    <Popover initialFocusRef={initialFocusRef} placement='bottom' closeOnBlur={false} >
      <PopoverTrigger>
        <Button>Rating</Button>
      </PopoverTrigger>
      <PopoverArrow bg='gray' />

      <PopoverContent color='white' bg='gray' borderColor='blue.800'  w="240px" h="fit-content" p="8px">
        
        <PopoverHeader display='flex' alignItems='center' justifyContent='flex-end'>
          <PopoverCloseButton w="10px" bg='gray'  />
        </PopoverHeader>
        
        <PopoverBody w="100%">
        <input type="range" min="1" max="10" step="1" className="btn1" value={btn1Value} onChange={onRatingChange} disabled={false} />
        <input type="range" min="0.1" max="0.9" step="1" className="btn2" value={btn2Value} onChange={onRatingChange} ref={btn2Ref} disabled={true} />
        </PopoverBody>
        
        <PopoverFooter border='0' display='flex' alignItems='center' justifyContent='space-between' pb={4} >
            <Button colorScheme='green' ref={initialFocusRef}>Apply</Button>
        </PopoverFooter>
      </PopoverContent>
    </Popover>
  )
}
