import React, { useState, useEffect, useRef } from "react";
import { useDispatch } from 'react-redux';
import { getMoviesBySearch } from "../../api/movies";
import { debounce } from "lodash";

export default function Navbar() {
  const dispatch = useDispatch();
  const [isFixed, setIsFixed] = useState(false);
  const headerRef = useRef(null);

  useEffect(() => {
    const headerElement = headerRef.current;

    const observer = new IntersectionObserver(
      ([entry]) => {
        // Trigger fixed state when the header is scrolled past the top
        if (!entry.isIntersecting) {
          setIsFixed(true);
        } else {
          setIsFixed(false);
        }
      },
      {
        threshold: [0], // Trigger when any part of the header is out of view
      }
    );

    if (headerElement) {
      observer.observe(headerElement);
    }

    return () => {
      if (headerElement) {
        observer.unobserve(headerElement);
      }
    };
  }, []); // Empty dependency array to run effect only once


  const onSearchChange = debounce((e) => {
    dispatch(getMoviesBySearch(e.target.value));
  }, 500);

  return (
    <header ref={headerRef} className={`navbar ${isFixed ? 'fixed' : ''}`}>
      <h1 id="logo">Movie Search App</h1>
      <div>
        <input 
          type="text" 
          maxLength="40" 
          onChange={onSearchChange} 
          placeholder="Search movies..." 
        />
        <button>Watchlist</button>
      </div>
    </header>
  );
}
