import { createSlice } from "@reduxjs/toolkit";
import { getwatchlist } from "../../Reducers/listedmovies";

const initialState = {
    listedmovies: [],
    movies: []
}

const watchlistSlice = createSlice({
    name: 'listedmovies',
    initialState,
    reducers: {
        addMovies: (state, action) => {
            state.listedmovies.push(action.payload);
        },
        removeMovies: (state, action) => {
            state.listedmovies = state.listedmovies.filter((item) => item !== action.payload);
        },
        getwatchlistmovie: (state, action) => {
            state.movies = action.payload;
        }
    }
});


export const { addMovies, removeMovie, getwatchlistmovie } = watchlistSlice.actions;

export default watchlistSlice.reducer;
