import React, { useState, useEffect, useRef } from "react";
import { useDispatch } from 'react-redux';
import { getMoviesBySearch } from "../../api/movies";
import { debounce } from "lodash";

export default function Navbar() {
  const dispatch = useDispatch();
  const [isFixed, setIsFixed] = useState(false);
  const headerRef = React.createRef();
  const logosRef = useRef(null);
  const FilterRef = useRef(null);

  useEffect(() => {
    const handleScroll = () => {
      const navHeight = headerRef.current.offsetHeight;
      console.log(window.scrollY,navHeight)
      if (window.scrollY > navHeight+ 200) {
        setIsFixed(true);
      } else {
        setIsFixed(false);
      }
    };

    window.addEventListener('scroll', handleScroll);

    return () => window.removeEventListener('scroll', handleScroll);
  }, [headerRef]);

  const handleLogos Â  
 = () => {
    logosRef.current.style.display = isFixed ? 'inline' : 'none';
    FilterRef.current.style.display = isFixed ? 'inline' : 'none';
  };

  useEffect(() => {
    handleLogos();
  }, [isFixed]);

  const onSearchChange = debounce((e) => {
    dispatch(getMoviesBySearch(e.target.value));
  }, 500);

  return (
    <header ref={headerRef} className={`navbar ${isFixed ? 'fixed' : ''}`}>
      <h1 ref={logosRef} id="logo">Movie Search App</h1>
      <div>
        <input
          type="text"
          maxLength="40"
          onChange={onSearchChange}
          placeholder="Search movies..."
        />
        <button>Watchlist</button>
      </div>
      <div>
        <button ref={FilterRef} >Filter By</button>
      </div>
    </header>
  );
}
