import { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import Navbar from "../../components/Navbar";
import MovieCard from "../../components/MovieCard";
import Swiper from "../../components/swiper";
import { getwatchlist } from "../../Reducers/listedmovies";
import no_movie from '../../assets/Movie-Hollywood-PNG-File.png'

const Watchlist = () => {
  const dispatch = useDispatch();
  const { movies} = useSelector(state => state.movies); // Assuming totalMovies exists in Redux state
  const { listedmovies} = useSelector(state => state.listedmovies);
  const { wmovies} = useSelector(state => state.listedmovies); // Assuming totalMovies exists in Redux state



  useEffect(() => {
    dispatch(getwatchlist(movies,listedmovies));
  }, [listedmovies]);

  return (
    <>
      <div className="container">
        <Swiper />
      </div>
      <div className="container1">
        <Navbar />
        <section className='content' >
          {wmovies?.length > 0 ? (wmovies.map((wmovie) => (<MovieCard movie={wmovie} />))) : 
          (<div style={{width:'85vw', height:'300px', textAlign:'center', display:'flex', flexDirection:'column', justifyContent:'center', alignItems:'center'}}><span style={{ fontSize:'32px', color:'white', opacity:'0.5', marginTop:'30px' }} >No movie in WatchList</span><img src={no_movie} alt='no_movie_logo' style={{width:'200px', marginTop:'10px'}}/></div> )}
        </section>
      </div>
    </>
  );
};

export default Watchlist;
