import React, { useState,  useRef } from "react";
import { useDispatch,} from 'react-redux';
import { getMoviesByRating } from "../../../api/movies"
import { getMovies } from "../../../api/movies";
import { debounce } from "lodash";
import {
  Popover,
  PopoverTrigger,
  PopoverContent,
  PopoverHeader,
  PopoverBody,
  PopoverFooter,
  PopoverArrow,
  PopoverCloseButton,
  Button
} from '@chakra-ui/react'

export default function Popup() {

  const dispatch = useDispatch();
  const initialFocusRef = useRef()
  const [btn1Value, setBtn1Value] = useState(1);
  const [btn2Value, setBtn2Value] = useState(0.1);
 

  const onRatingChange = (event) => {
    const target = event.target;

    if (target.className === 'btn1') {
      setBtn1Value(parseInt(target.value));
    } else if (target.className === 'btn2') {
      setBtn2Value(parseFloat(target.value));
    }  
  };

  const onRatingSearch = () => {
    const rating = btn1Value + btn2Value;
    console.log(rating)
    dispatch(getMoviesByRating(rating));
  };

  const onRatingreset = () => {
    setBtn1Value("1");
    setBtn2Value("0.1");
    dispatch( getMovies());
  };
  //dispatch(getMoviesByRating(btn2Value));


  return (
    <Popover initialFocusRef={initialFocusRef} placement='bottom' closeOnBlur={false} >
      <PopoverTrigger>
        <Button>Rating</Button>
      </PopoverTrigger>
      <PopoverArrow bg='red' />

      <PopoverContent color='white' bg='gray' borderColor='blue.800'  w="240px" h="fit-content" p="8px">
        
        <PopoverHeader display='flex' alignItems='center' justifyContent='flex-end'>
          <PopoverCloseButton w="10px" bg='gray'  />
        </PopoverHeader>
        
        <PopoverBody w="100%">
        <input type="range" min="1" max="10" step="1" className="btn1" value={btn1Value} onChange={onRatingChange} />
        <input type="range" min="0.1" max="0.9"  step="0.1" className="btn2" value={btn2Value} onChange={onRatingChange} />
        </PopoverBody>
        
        <PopoverFooter border='0' display='flex' alignItems='center' justifyContent='space-between' pb={4} >
            <Button colorScheme='green' onClick={onRatingSearch}>Apply</Button>
        </PopoverFooter>
      </PopoverContent>
    </Popover>
  )
}
