import * as React from 'react';
import "./index.css";



export default function MoviesCard({movie}) {
  
  const {id,rank,title,description,genres,rating,ranking,year,imdb_id,imdb_url,image_url,director,writer,duration,age_certification} = movie;
  const genreString = Array.isArray(genres) ? genres.join(', ') : '';
  console.log(genreString, genres)
  
  return (
    <div className="card" id={movie.id} key={movie.id}>
      <img className='poster' src={image_url} alt={`image${id}`}/>
      <div  style={{ display: 'flex', justifyContent: "space-between", marginBottom: "8px"}} >
        <div>{title}</div>
        <div>{year}</div>
      </div>
      <div>
        <span>Genre:</span>
        <span>genres.map(genre => genre).join(', ')</span>
      </div>
      <div><span>Director:</span><span>{director}</span></div>
      

    </div>


  );
}
