import * as React from "react";
import { useState, useEffect, useRef } from "react";
import { useDispatch, useSelector } from 'react-redux';
import { getMoviesBySearch } from "../../api/movies";
import { getAllGenres } from "../../api/movies";
import { getMoviesByRating } from "../../api/movies";
import {debounce} from "lodash";

export default function Navbar() {

  const dispatch = useDispatch();

  const [isFixed, setIsFixed] = useState(false);
  const headerRef = useRef(null);

  useEffect(() => {
    const observer = new IntersectionObserver(([entry]) => {
      setIsFixed(!entry.isIntersecting);
    }, {
      rootMargin: '10px'
    });

    if (headerRef.current) {
      observer.observe(headerRef.current);
    }

    return () => observer.disconnect();
  }, []);

  const onSearchChange = debounce((e) => {
    dispatch(getMoviesBySearch(e.target.value))
  }, 500)

  return (
    <header ref={headerRef} className={`header ${isFixed ? 'fixed' : ''}`}>
      <h1 id="logo"> Movie Search app</h1>
        <div>
          < input type ="text" maxLength="40" onChange={onSearchChange}/>
          <button>Watchlist</button>
        </div>
        <div>
          <button>Watchlist</button>
        </div>
    </header>
  );
}
