import * as React from 'react';
import { getMoviesByGenre } from '../../api/movies';
import { useDispatch, useSelector } from 'react-redux';
import { getMoviesBySearch } from "../../api/movies";
import { getAllGenres } from "../../api/movies";
import { getMoviesByRating } from "../../api/movies";
import {debounce} from "lodash";
import { useEffect } from "react";


  

export default function SelectorComponent({name, value}) {
  const dispatch = useDispatch();
  const [selectedvalue, setselectedvalue] = React.useState('');

  const handleChange = (event) => {
    setselectedvalue(event.target.value);
    dispatch(getMoviesByGenre(event.target.value));

    /*  useEffect(() => {
    dispatch(getAllGenres())
  }, [])

  const onRatingChange = debounce((e) => {
    dispatch(getMoviesByRating(e.target.value))
    console.log(e.target.value);
  }, 1000)  


  const { genres, rating } = useSelector(state => state.movies);*/
  }; 

  return (
    <Box sx={{ minWidth: 120 }}> 
      <FormControl fullWidth>
        <InputLabel id="demo-simple-select-label">{name}</InputLabel>
        <Select
          labelId="demo-simple-select-label"
          id="demo-simple-select"
          value={selectedvalue}
          label={name}
          onChange={handleChange}
        >
          {
            value?.length > 0 && value.map(item => <MenuItem value={item}>{item}</MenuItem> )
          }
        </Select>
      </FormControl>
    </Box>
  );
}
