import React, { useState, useEffect, useRef } from "react";
import { useDispatch } from 'react-redux';
import { getMoviesBySearch } from "../../api/movies";
import { debounce } from "lodash";

export default function Navbar() {
  const dispatch = useDispatch();
  const [isFixed, setIsFixed] = useState(false);
  const headerRef = useRef(null);

  useEffect(() => {
    const observer = new IntersectionObserver(([entry]) => {
      setIsFixed(!entry.isIntersecting);
    }, {
      threshold: [0], // Trigger when any part of the header is out of view
    });

    if (headerRef.current) {
      observer.observe(headerRef.current);
      console.log("run")
    }

    return () => {
      if (headerRef.current) {
        observer.unobserve(headerRef.current);
      }
    };
  }, [isFixed]);

  const onSearchChange = debounce((e) => {
    dispatch(getMoviesBySearch(e.target.value));
  }, 500);

  return (
    <header ref={headerRef} className={`navbar ${isFixed ? 'fixed' : ''}`}>
      <h1 id="logo">Movie Search App</h1>
      <div>
        <input 
          type="text" 
          maxLength="40" 
          onChange={onSearchChange} 
          placeholder="Search movies..." 
        />
        <button>Watchlist</button>
      </div>
    </header>
  );
}
