import React, { useState, useRef } from "react";
import { useDispatch } from 'react-redux';
import { getMoviesByfilter } from "../../../api/movies";
import {
  Popover,
  PopoverTrigger,
  PopoverContent,
  PopoverHeader,
  PopoverBody,
  PopoverFooter,
  PopoverArrow,
  PopoverCloseButton,
  Button
} from '@chakra-ui/react';

export default function Popup() {
  const dispatch = useDispatch();
  const initialFocusRef = useRef();
  const [rating, setRating] = useState(1.0);

  const onRatingChange = (event) => {
    setRating(parseFloat(event.target.value));
  };

  const onRatingSearch = () => {
    console.log(rating);
    let value = `rating:${rating}`;
    dispatch(getMoviesByfilter(value));
  };

  const onRatingReset = () => {
    setRating(1.0);
    dispatch(getMoviesByfilter("1"));
  };

  return (
    <Popover initialFocusRef={initialFocusRef} placement='bottom' closeOnBlur={false} >
      <PopoverTrigger>
        <Button w="fit-content" h="fit-content" fontSize='14px' fontWeight='400' color="#F5F7F8" padding='10px 5px 10px 5px' margin='5px' backgroundColor='transparent' borderRadius='4px' _hover={{ backgroundColor: "#17153B", color: "#525CEB"}}>{rating === 1.0 ? "Rating" : rating.toFixed(1)}</Button>
      </PopoverTrigger>

      <PopoverContent color='white' bg='#383838'  w="240px" h="fit-content" p="8px" borderRadius='4px'>
        <PopoverArrow bg='#383838' />
        <PopoverHeader display='flex' alignItems='center' justifyContent='flex-end' m='0px'>
          <PopoverCloseButton w="10px" bg='transparent'  />
        </PopoverHeader>

        <PopoverBody w='100%'>
          <input type="range" min="1.0" max="10.0" step="0.1"  style={{ width:'100%', height:'8px'}} onChange={onRatingChange} />
          <div style={{ display:'flex', justifyContent:'space-evenly', w:'100%'}} >
          <span>1</span>
          <span>2</span>
          <span>3</span>
          <span>4</span>
          <span>5</span>
          <span>6</span>
          <span>7</span>
          <span>8</span>
          <span>9</span>
          <span>10</span>
          </div>
        </PopoverBody>

        <PopoverFooter border='0' display='flex' alignItems='center' justifyContent='space-between' pb={4} >
          <Button colorScheme='green' onClick={onRatingSearch}>Apply</Button>
          <Button colorScheme='green' onClick={onRatingReset}>Reset</Button>
        </PopoverFooter>
      </PopoverContent>
    </Popover>
  );
}
