// Pagination.js
import React from 'react';

const Pagination = ({ totalPages, currentPage, handlePageChange }) => {
  if (totalPages <= 1) return null; // No pagination needed if only one page

  const pageNumbers = [];
  for (let i = 1; i <= totalPages; i++) {
    pageNumbers.push(i);
  }

  return (
    <div className="pagination" style={{ margin: '30px 30px 0px 30px', display: 'flex'}}>
      {currentPage > 1 && ( // Previous button if not on first page
        <span onClick={() => handlePageChange(currentPage - 1)}>
          <button>Previous</button>
        </span>
      )}
      {pageNumbers.map((pageNumber) => (
        <span key={pageNumber} className={pageNumber === currentPage ? "active" : ""}>
          <button style={{ width: '25px',  backgroundColor: 'white', display: 'flex' }} onClick={() => handlePageChange(pageNumber)}>{pageNumber}</button>
        </span>
      ))}
      {currentPage < totalPages && ( // Next button if not on last page
        <span onClick={() => handlePageChange(currentPage + 1)}>
          <button>Next</button>
        </span>
      )}
    </div>
  );
};

export default Pagination;
