import * as React from 'react';
import star from "../../assets/star.svg"
import { useDispatch, useSelector } from "react-redux";
import "./index.css";
import { addMovies } from '../../slice/watchlistSlice/watchlistSlice';

export default function MoviesCard({ movie }) {

  const dispatch = useDispatch();
  const {
    id,
    rank,
    title,
    description,
    genres,
    ratings,
    ranking,
    year,
    imdb_id,
    imdb_url,
    image_url,
    director,
    writer,
    duration,
    age_certification,
  } = movie;

  const formattedGenres = genres.replace(/\[|\]|'/g, '').replace(/\s*,\s*/g, ',');
  const { listedmovies} = useSelector(state => state.listedmovies);
  

  /*const getMoviesId = (event) => {
    dispatch(addMovies(event.target.value))
  }*/

  console.log("moviecard:",listedmovies)


  return (
    <div className="card" id={`movie-${id}`} key={`movie-${id}`}>
      <img
        className="poster"
        src={image_url}
        alt={`image ${id}`}
        role="img"
        aria-label={title}
      />
      <button id='watchlist' value={id} >Add to watchlist</button>
      <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '8px' }}>
        <div>{title}</div>
        <div>{year}</div>
      </div>
      <div>
        <span>Genre: </span>
        <span>{formattedGenres}</span>
      </div>
      <div>
        <span>Director: </span>
        <span>{director}</span>
      </div>
      <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '8px' }}>
        <span><img src={star} alt="star.logo"/>{ratings}</span>
        <button>Learn More</button>
      </div>
    </div>
  );
}
