import { createSlice } from "@reduxjs/toolkit";

const initialState = {
    listedmovies: []
}

const watchlistSlice = createSlice({
    name: 'listedmovies',
    initialState,
    reducers: {
        addMovies: (state, action) => {
            state.listedmovies= action.payload;
        }
    }
});


export const { addMovies } = watchlistSlice.actions;

export default watchlistSlice.reducer;
