import { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getMovies } from "../../api/movies";
import Navbar from "../../components/Navbar";
import MovieCard from "../../components/MovieCard";
import Swiper from "../../components/swiper";
import { Pagination } from "@nextui-org/react";



const Home = () => {

  const dispatch = useDispatch();
  const { movies } = useSelector(state => state.movies);
  const [pageNumber, setPageNumber] = useState(1); // Add a state to store the current page number

  useEffect(() => {
    dispatch(getMovies(pageNumber)); // Pass the current page number to the getMovies action
  }, [pageNumber]);

  const handlePageChange = (newPageNumber) => {
    setPageNumber(newPageNumber);
  };


  useEffect(() => {
    dispatch(getMovies());
  }, []);

  console.log(movies)


  return (
        <>
          <div className="container">
            <Swiper/>
          </div>
          <div class="container1">
            <Navbar/>
            <section>
            {
            (movies?.length > 0) ? movies.map(movie => <MovieCard keys={movie.id} movie={movie}/>) : <><div></div></> 
            }
            
            <Paginations
            currentPage={currentPage}
            totalPages={totalPages}
            onPageChange={handlePageChange}
          />
            </section>
            
          </div>
        </>
    )
}

export default Home;
