/*import { getAllGenres } from "../../api/movies";
import { getMoviesByRating } from "../../api/movies";*/
import React, { useState, useEffect, useRef } from "react";
import { useDispatch } from 'react-redux';
import { getMoviesBySearch } from "../../api/movies";
import { debounce } from "lodash";
import logo from "../../assets/oie_transparent.png"
import "./index.css"
import "./watchlist.css"
import Dropdown from "../../components/Dropdown";


export default function Navbar() {
  const dispatch = useDispatch();
  const [isFixed, setIsFixed] = useState(false);
  const HeaderRef = React.createRef();
  const FilterRef = useRef(null);
  const WatchListRef = useRef(null);
  const IconContainerRef = useRef(null);
  const textRef = useRef(null);

  useEffect(() => {
    const handleScroll = () => {
      const navHeight = HeaderRef.current.offsetHeight;
      console.log(navHeight);
      if (window.scrollY > navHeight+ 300) {
        setIsFixed(true);
      } else {
        setIsFixed(false);
      }
    };

    window.addEventListener('scroll', handleScroll);

    return () => window.removeEventListener('scroll', handleScroll);
  }, [HeaderRef]);

  const handleLogos Â  
 = () => {
    FilterRef.current.style.display = isFixed ? 'inline' : 'none';
    IconContainerRef.current.style.display = isFixed ? 'none' : '';
    
  };

  useEffect(() => {
    handleLogos();
  }, [isFixed]);

  const onSearchChange = debounce((e) => {
    dispatch(getMoviesBySearch(e.target.value));
  }, 500);

  return (
    <header ref={HeaderRef} className={`navbar ${isFixed ? 'fixed' : ''}`}>
      <img id="logo" src={logo} alt="logo"/>
      <div className="group">

        <svg viewBox="0 0 24 24" aria-hidden="true" class="icon">
          <g><path d="M21.53 20.47l-3.66-3.66C19.195 15.24 20 13.214 20 11c0-4.97-4.03-9-9-9s-9 4.03-9 9 4.03 9 9 9c2.215 0 4.24-.804 5.808-2.13l3.66 3.66c.147.146.34.22.53.22s.385-.073.53-.22c.295-.293.295-.767.002-1.06zM3.5 11c0-4.135 3.365-7.5 7.5-7.5s7.5 3.365 7.5 7.5-3.365 7.5-7.5 7.5-7.5-3.365-7.5-7.5z"></path></g>
        </svg>
        <input type="text" maxLength="40" onChange={onSearchChange} placeholder="Search the movies" />

        <button ref={WatchListRef} className={`${isFixed ? 'btn' : 'WatchListBtn'}`}>
          <span ref={IconContainerRef} className="IconContainer">
            <svg viewBox="0 0 384 512" height="0.9em" className="icon1">
              <path d="M0 48V487.7C0 501.1 10.9 512 24.3 512c5 0 9.9-1.5 14-4.4L192 400 345.7 507.6c4.1 2.9 9 4.4 14 4.4c13.4 0 24.3-10.9 24.3-24.3V48c0-26.5-21.5-48-48-48H48C21.5 0 0 21.5 0 48z" ></path>
            </svg>
          </span>
          <p ref={textRef} className={`${isFixed ? '' : 'text'}`}>WatchList</p>
        </button>
      </div>
      <div ref={FilterRef} >
        <label>Filter by: </label>
        <Dropdown/>
      </div>
    </header>
  );
}
