// Pagination.js
import React from 'react';

const Pagination = ({ totalPages, currentPage, handlePageChange }) => {
  if (totalPages <= 1) return null; // No pagination needed if only one page

  const pageNumbers = [];
  for (let i = 1; i <= totalPages; i++) {
    pageNumbers.push(i);
  }

  return (
    <div className="pagination" style={{ fontSize: '16px',  margin: '30px 30px 0px 30px', display: 'flex', justifyContent: 'center', alignItems: 'center', gap: '18px', backgroundColor: 'transparent'}}>
      {currentPage > 1 && ( // Previous button if not on first page
        <span onClick={() => handlePageChange(currentPage - 1)}>
          <button style={{ width: '65px', height: '30px', color: 'white', padding: '2px 5px 2px 5px', display: 'flex', justifyContent: 'center', alignItems: 'center', borderRadius: '4px'}} >Previous</button>
        </span>
      )}
      {pageNumbers.map((pageNumber) => (
        <span key={pageNumber} className={pageNumber === currentPage ? "" : ""}>
          <button style={{ width: '30px', height: '30px', color: 'white', padding: '2px 5px 2px 5px', borderRadius: '4px' }} onClick={() => handlePageChange(pageNumber)}>{pageNumber}</button>
        </span>
      ))}
      {currentPage < totalPages && ( // Next button if not on last page
        <span onClick={() => handlePageChange(currentPage + 1)}>
          <button style={{ color: 'white', height: '30px',  display: 'flex', justifyContent: 'center', alignItems: 'center', padding: '2px 5px 2px 5px', borderRadius: '4px'}}>Next</button>
        </span>
      )}
    </div>
  );
};

export default Pagination;
