import React, { useState,  useRef } from "react";
import { useDispatch,} from 'react-redux';
import { getMoviesByfilter } from "../../../api/movies"
import {
  Popover,
  PopoverTrigger,
  PopoverContent,
  PopoverHeader,
  PopoverBody,
  PopoverFooter,
  PopoverArrow,
  PopoverCloseButton,
  Button
} from '@chakra-ui/react'

export default function Popup() {

  const dispatch = useDispatch();
  const initialFocusRef = useRef()
  const [btn1Value, setBtn1Value] = useState(1);
  const [btn2Value, setBtn2Value] = useState(0);
  const rating = btn1Value + btn2Value;
 

  const onRatingChange = (event) => {
    const target = event.target;

    if (target.className === 'btn1') {
      setBtn1Value(parseInt(target.value));
    } else if (target.className === 'btn2') {
      setBtn2Value(parseFloat(target.value));
    }  
  };

  const onRatingSearch = () => {
    console.log(rating)
    let value = `rating:${rating}`
    dispatch(getMoviesByfilter(value));
  };

  const onRatingReset = () => {
    setBtn1Value(1);
    setBtn2Value(0);
    dispatch( getMoviesByfilter("1"));
  };


  return (
    <Popover initialFocusRef={initialFocusRef} placement='bottom' closeOnBlur={false} >
      <PopoverTrigger>
        <Button w="fit-content" h="fit-content" fontSize='14px' fontWeight='400' padding='5px 2px 5px 2px' margin='5px' background-color='transparent' border-radius= '4px'>{(rating<=1.1)? "rating" : rating}</Button>
      </PopoverTrigger>
      <PopoverArrow bg='red' />

      <PopoverContent color='white' bg='gray' borderColor='blue.800'  w="240px" h="fit-content" p="8px">
        
        <PopoverHeader display='flex' alignItems='center' justifyContent='flex-end'>
          <PopoverCloseButton w="10px" bg='gray'  />
        </PopoverHeader>
        
        <PopoverBody w="100%">
        <input type="range" min="1.0" max="9.0" step="1" className="btn1" value={btn1Value} onChange={onRatingChange} />
        <input type="range" min="0" max="0.9"  step="0.1" className="btn2" value={btn2Value} onChange={onRatingChange} />
        </PopoverBody>
        
        <PopoverFooter border='0' display='flex' alignItems='center' justifyContent='space-between' pb={4} >
            <Button colorScheme='green' onClick={onRatingSearch}>Apply</Button>
            <Button colorScheme='green' onClick={onRatingReset}>Reset</Button>
        </PopoverFooter>
      </PopoverContent>
    </Popover>
  )
}
