import * as React from 'react';
import Card from '@mui/material/Card';
import CardActions from '@mui/material/CardActions';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import Button from '@mui/material/Button';
import Typography from '@mui/material/Typography';
import { useDispatch, useSelector } from 'react-redux';
import { addMovie } from '../../slice/movieSlice';

export default function moviesCard({movie}) {

    const {id,rank,title,description,genres,rating,ranking,year,imdb_id,imdb_url,image_url,director,writer,duration,age_certification} = movie;

    const dispatch = useDispatch();

    const onAddtowatchlist = () => {
      dispatch(addMovie(movie));
    }

    const { filtermovie } = useSelector(state => state.filtermovie);


  return (
    <Card sx={{ maxWidth: 250 }}>
      <CardMedia
        component="img"
        alt="green iguana"
        height="400"
        image= {image_url}
      />
      <CardContent>
        <Typography gutterBottom variant="h5" component="div">
          {title}
        </Typography>
        <Typography variant="body2" color="text.secondary">{description}
        </Typography>
      </CardContent>
      <CardActions>
        <Button size="small" onClick={onAddtowatchlist} >Add to watchlist</Button>
        <Button size="small">Learn More</Button>
      </CardActions>
    </Card>
  );
}
