import * as React from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { getMoviesByRating } from "../../../api/movies"
import { debounce } from "lodash";
import {
  Popover,
  PopoverTrigger,
  PopoverContent,
  PopoverHeader,
  PopoverBody,
  PopoverFooter,
  PopoverArrow,
  PopoverCloseButton,
  PopoverAnchor,
  Box,Button,ButtonGroup
} from '@chakra-ui/react'

export default function Popup() {

  const dispatch = useDispatch();
  const { rating } = useSelector((state) => state.movies);
  
  const onRatingChange = debounce((e) => {
  dispatch(getMoviesByRating(e.target.value));
  console.log(e.target.value);
  }, 1000);
  const initialFocusRef = React.useRef()
  return (
    <Popover
      initialFocusRef={initialFocusRef}
      placement='bottom'
      closeOnBlur={false}
    >
      <PopoverTrigger>
        <Button>Rating</Button>
      </PopoverTrigger>
      <PopoverContent color='white' bg='gray' borderColor='blue.800'  w="240px" h="fit-content" p="5px">
        <PopoverArrow bg='gray' />

        <PopoverHeader display='flex' alignItems='center' justifyContent='flex-end'>
        <PopoverCloseButton w="20px"  />
        </PopoverHeader>
        
        <PopoverBody maxW="200px" w="100%">
          Lorem ipsum dolor sit amet, consectetur adipisicing elit, sed do
          eiusmod tempor incididunt ut labore et dolore.
        </PopoverBody>
        <PopoverFooter
          border='0'
          display='flex'
          alignItems='center'
          justifyContent='space-between'
          pb={4}
        >
          <ButtonGroup size='sm'>
            <Button colorScheme='green'>Setup Email</Button>
            <Button colorScheme='blue' ref={initialFocusRef}>
              Next
            </Button>
          </ButtonGroup>
        </PopoverFooter>
      </PopoverContent>
    </Popover>
  )
}
