import * as React from 'react';
import { useState, useEffect } from 'react';
import { useDispatch, useSelector } from "react-redux";
import { addMovies, removeMovies } from '../../slice/watchlistSlice/watchlistSlice';
import Detail from '../../components/Modal';
import star from "../../assets/star.svg"
import "./index.css";



export default function MoviesCard({ movie }) {

  const dispatch = useDispatch();
  const { listedmovies} = useSelector(state => state.listedmovies);
  const [active0, setactive0] = useState(false);

  const {id,rank,title,genres,ratings,year,image_url,director} = movie;
  const formattedGenres = genres.replace(/\[|\]|'/g, '').replace(/\s*,\s*/g, ',');


  const getMoviesId = (event) => {
    const movieId = event.target.value;
    const isMovieListed = listedmovies.includes(movieId);
  
    if (isMovieListed) {
      dispatch(removeMovies(movieId));

    } else {
      dispatch(addMovies(movieId));
    }
  };

  useEffect(() => {
    const isMovieListed = listedmovies.includes(movie.id);
    if (isMovieListed) {
      setactive0(false);
    } else {
      setactive0(true);;
    }
    
  }, [listedmovies]);

  return (
    <div className="card" id={`movie-${id}`} >
      <div className='poster'>
        <img src={image_url} alt={`image ${id}`} /><button className={`${active0 ? '' : 'fixed'}`} id='watchlist' value={id} onClick={getMoviesId} >Add to watchlist</button>
      <div className='rank'>{rank}</div>
      </div>
      <div className='detail'>
        <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '5px' }}>
          <div>{title}</div>
          <div>{year}</div>
        </div>
        <div>
          <span>Genre: </span>
          <span>{formattedGenres}</span>
        </div>
        <div>
          <span>Director: </span>
          <span>{director}</span>
        </div>
        <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '8px' }}>
          <span><img src={star} alt="star.logo"/>{ratings}</span>
          <Detail movie={ movie} genres={formattedGenres} />
        </div>
      </div>
    </div>
  );
}
