import React, { useState, useEffect, useRef } from "react";
import { useDispatch } from 'react-redux';
import { getMoviesBySearch } from "../../api/movies";
import { debounce } from "lodash";
import logo from "../../assets/oie_transparent.png"


export default function Navbar() {
  const dispatch = useDispatch();
  const [isFixed, setIsFixed] = useState(false);
  const headerRef = React.createRef();
  const FilterRef = useRef(null);

  useEffect(() => {
    const handleScroll = () => {
      const navHeight = headerRef.current.offsetHeight;
      if (window.scrollY > navHeight+ 250) {
        setIsFixed(true);
      } else {
        setIsFixed(false);
      }
    };

    window.addEventListener('scroll', handleScroll);

    return () => window.removeEventListener('scroll', handleScroll);
  }, [headerRef]);

  const handleLogos Â  
 = () => {
    FilterRef.current.style.display = isFixed ? 'inline' : 'none';
  };

  useEffect(() => {
    handleLogos();
  }, [isFixed]);

  const onSearchChange = debounce((e) => {
    dispatch(getMoviesBySearch(e.target.value));
  }, 500);

  return (
    <header ref={headerRef} className={`navbar ${isFixed ? 'fixed' : ''}`}>
      <img id="logo" src={logo} alt="logo"/>
      <div class="group">
        <svg viewBox="0 0 24 24" aria-hidden="true" class="icon">
          <g>
            <path d="M21.53 20.47l-3.66-3.66C19.195 15.24 20 13.214 20 11c0-4.97-4.03-9-9-9s-9 4.03-9 9 4.03 9 9 9c2.215 0 4.24-.804 5.808-2.13l3.66 3.66c.147.146.34.22.53.22s.385-.073.53-.22c.295-.293.295-.767.002-1.06zM3.5 11c0-4.135 3.365-7.5 7.5-7.5s7.5 3.365 7.5 7.5-3.365 7.5-7.5 7.5-7.5-3.365-7.5-7.5z"></path>
          </g>
        </svg>
        <input type="text" maxLength="40" onChange={onSearchChange} placeholder="Search the movies" />
      </div>
        <button className="button">/* From Uiverse.io by satyamchaudharydev */ 
<button>
  <div class="text">
    <span>Back</span>
    <span>to</span>
    <span>top</span>
  </div>
  <div class="clone">
    <span>Back</span>
    <span>to</span>
    <span>top</span>
  </div>
  <svg
    stroke-width="2"
    stroke="currentColor"
    viewBox="0 0 24 24"
    fill="none"
    class="h-6 w-6"
    xmlns="http://www.w3.org/2000/svg"
    width="20px"
  >
    <path
      d="M14 5l7 7m0 0l-7 7m7-7H3"
      stroke-linejoin="round"
      stroke-linecap="round"
    ></path>
  </svg>
</button>
</button>
      <div>
        <button id="Filter" ref={FilterRef} >Filter By</button>
      </div>
    </header>
  );
}
