import * as React from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { getMoviesByRating } from "../../api/movies";
import { debounce } from "lodash";
import { Popover, PopoverButton, PopoverPanel } from '@headlessui/react';

export default function Popup() {
    const dispatch = useDispatch();
    
    const onRatingChange = debounce((e) => {
    dispatch(getMoviesByRating(e.target.value));
    console.log(e.target.value);
    }, 1000);
    
    const { rating } = useSelector((state) => state.movies);

  return (
  <Popover>
        <PopoverButton className="block text-sm/6 font-semibold text-white/50 focus:outline-none data-[active]:text-white data-[hover]:text-white data-[focus]:outline-1 data-[focus]:outline-white">Rating</PopoverButton>
        <PopoverPanel transition anchor="bottom" className="divide-y divide-white/5 rounded-xl bg-white/5 text-sm/6 transition duration-200 ease-in-out [--anchor-gap:var(--spacing-5)] data-[closed]:-translate-y-1 data-[closed]:opacity-0">
        <div class="slider">
            <input type="range" min="0" max="10" value="5"/>
            <div class="labels">
                <span>0</span>
                <span>5</span>
                <span>10</span>
            </div>
        </div>
        <div class="slider">
            <input type="range" min="0" max="10" value="5"/>
            <div class="labels">
              <span>0</span>
              <span>5</span>
              <span>10</span>
            </div>
        </div>
        
        </PopoverPanel>
    </Popover>
  );
}
