import * as React from "react";
import { useDispatch, useSelector } from 'react-redux';
import { getMoviesBySearch } from "../../api/movies";
import { getAllGenres } from "../../api/movies";
import { getMoviesByRating } from "../../api/movies";
import {debounce} from "lodash";
import { useEffect } from "react";

export default function Navbar() {

  const dispatch = useDispatch();

  const onSearchChange = debounce((e) => {
    dispatch(getMoviesBySearch(e.target.value))
  }, 500)

  useEffect(() => {
    dispatch(getAllGenres())
  }, [])

  const onRatingChange = debounce((e) => {
    dispatch(getMoviesByRating(e.target.value))
    console.log(e.target.value);
  }, 1000)  

  const { genres, rating } = useSelector(state => state.movies);


  return (
    <header>
      <h1>Movies App</h1>
      <div>< input type ="text" maxLength="40" onChange={onSearchChange}/></div>
    </header>
  );
}
