import React, { useState, useEffect, useRef } from "react";
import { useDispatch } from 'react-redux';
import { getMoviesBySearch } from "../../api/movies"; // Make sure this is the correct file
import { debounce } from "lodash";

export default function Navbar() {
  const dispatch = useDispatch();
  const [isFixed, setIsFixed] = useState(false);
  const headerRef = useRef(null);

  useEffect(() => {
    const handleScroll = () => {
      const navHeight = headerRef.current.getBoundingClientRect();
      const isAtTop = navHeight.top <= 0;
      setIsFixed(isAtTop);
      console.log("run", setIsFixed());
    };

    window.addEventListener('scroll', handleScroll);

    return () => window.removeEventListener('scroll', handleScroll);
  }, [isFixed]);

  const onSearchChange = debounce((e) => {
    dispatch(getMoviesBySearch(e.target.value));
  }, 500); // Pass the function to be debounced as the first argument, and the delay as the second argument

  return (
    <header ref={headerRef} className={`navbar ${isFixed ? 'fixed' : ''}`}>
      {isFixed && <h1 id="logo">Movie Search App</h1>}
      <div>
        <input
          type="text"
          maxLength="40"
          onChange={onSearchChange}
          placeholder="Search movies..."
        />
        <button>Watchlist</button>
      </div>
    </header>
  );
}
