import React, { useState, useEffect, useRef } from "react";
import { useDispatch } from 'react-redux';
import { getMoviesBySearch } from "../../api/movies"; // Make sure this is the correct file
import { debounce } from "lodash";

export default function Navbar() {

  const dispatch = useDispatch();
  const [isHeaderAtTop, setIsHeaderAtTop] = useState(false);
  const headerRef = useRef(null);
  const logosRef = useRef(null);
  const [isFixed, setIsFixed] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      const headerRect = headerRef.current.getBoundingClientRect();
      const isAtTop = headerRect.top <= 0;
      setIsHeaderAtTop(isAtTop);
    };

    window.addEventListener('scroll', handleScroll);

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

 const handleLogos Â  
 = () => {
    logosRef.current.style.display = isFixed ? 'inline' : 'none';
  };

  useEffect(() => {
    handleLogos();
  }, [isFixed]);

  const onSearchChange = debounce((e) => {
    dispatch(getMoviesBySearch(e.target.value));
  }, 500); // Pass the function to be debounced as the first argument, and the delay as the second argument

  return (
    <header ref={headerRef} className={`navbar ${isFixed ? 'fixed' : ''}`}>
      <h1 ref={logosRef} id="logo">Movie Search App</h1>
      <div>
        <input
          type="text"
          maxLength="40"
          onChange={onSearchChange}
          placeholder="Search movies..."
        />
        <button>Watchlist</button>
      </div>
    </header>
  );
}
