// Home.js
import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { getMovies } from '../../api/movies';
import { setcurrentPage } from '../../slice/movieSlice/movieSlice';
import Navbar from '../../components/Navbar';
import MovieCard from '../../components/MovieCard';
import Swiper from '../../components/swiper';
import Pagination from '../../components/Pagination';
import no_movie from '../../assets/Movie-film-disk-with-clapper-board-vector-PNG.png'

const ITEMS_PER_PAGE = 20; // Adjust this value as needed

const Home = () => {
  const dispatch = useDispatch();
  const { movies } = useSelector(state => state.movies); // Assuming totalMovies exists in Redux state
  const { currentPage } = useSelector(state => state.movies);
  const { listedmovies } = useSelector(state => state.listedmovies);

  const  [ dataload, setdataload ] = useState(true)
  const totalMovies = movies.length;

  const startIndex = (currentPage - 1) * ITEMS_PER_PAGE;
  const endIndex = Math.min(startIndex + ITEMS_PER_PAGE, movies.length); // Ensure endIndex doesn't exceed movie count

  const paginatedMovies = movies.slice(startIndex, endIndex); // Slice movies for current page

  const totalPages = Math.ceil(totalMovies / ITEMS_PER_PAGE); // Calculate total pages

  const handlePageChange = (pageNumber) => {
    dispatch(setcurrentPage(pageNumber));
    window.scrollTo(0, 0);
  };

  useEffect(() => {
    dispatch(getMovies());
  }, []);

  console.log(movies, listedmovies);

  return (
    <>
      <div className="container">
        <Swiper />
      </div>
      <div className="container1">
        <Navbar />
        <section className='content'>
          {(dataload) ? <div>Loading</div> :(movies?.length > 0 ? ( paginatedMovies.map((movie) => (<MovieCard key={movie.id} movie={movie} />))) : 
          (<div style={{width:'85vw', height:'500px', textAlign:'center', display:'flex', flexDirection:'column', justifyContent:'center', alignItems:'center'}}><span style={{ fontSize:'32px', color:'white', opacity:'0.5' }} >No movies found</span><img src={no_movie} alt='no_movie_logo' style={{width:'300px'}}/></div>))}
        </section>
        <Pagination
          totalPages={totalPages}
          handlePageChange={handlePageChange}
        />
      </div>
    </>
  );
};

export default Home;
