import * as React from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { getMoviesByRating } from "../../../api/movies"
import { debounce } from "lodash";
import { StatefulPopover } from "baseui/popover";
import { Input } from "baseui/input";
import { Button } from "baseui/button";

export default function Popup() {

  const dispatch = useDispatch();
  const { rating } = useSelector((state) => state.movies);
  
  const onRatingChange = debounce((e) => {
  dispatch(getMoviesByRating(e.target.value));
  console.log(e.target.value);
  }, 1000);


  return (
    <StatefulPopover
      content={() => (
        <div>
          <Input placeholder="Focusable Element" />
        </div>
      )}
      showArrow
      returnFocus
      autoFocus
      overrides={{
        Arrow: {
          style: ({ $theme }) => ({
            outline: `${$theme.colors.warning600} solid`,
            backgroundColor: $theme.colors.warning600
          })
        },
        Body: {
          style: ({ $theme }) => ({
            outline: `${$theme.colors.warning600} solid`,
            backgroundColor: $theme.colors.warning600
          })
        },
        Inner: {
          style: ({ $theme }) => ({
            outline: `${$theme.colors.warning600} solid`,
            backgroundColor: $theme.colors.warning600
          })
        }
      }}
    >
      <Button>Rating</Button>
    </StatefulPopover>

  );
}
