import * as React from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { getMoviesByRating } from "../../../api/movies"
import { debounce } from "lodash";
import { Popover, PopoverButton, PopoverPanel } from '@headlessui/react';

export default function Popup() {

  const dispatch = useDispatch();
  const { rating } = useSelector((state) => state.movies);
  
  const onRatingChange = debounce((e) => {
  dispatch(getMoviesByRating(e.target.value));
  console.log(e.target.value);
  }, 1000);


  return (
    <Popover>
        <PopoverButton className="block text-sm/6 font-semibold text-white/50 focus:outline-none data-[active]:text-white data-[hover]:text-white data-[focus]:outline-1 data-[focus]:outline-white">Rating</PopoverButton>
        <PopoverPanel transition anchor="bottom" className=" w-52 divide-y divide-white/5 rounded-xl bg-white/5 text-sm/6 transition duration-200 ease-in-out [--anchor-gap:var(--spacing-5)] data-[closed]:-translate-y-1 data-[closed]:opacity-1">
        <div className="p-3">
              <a className="block rounded-lg py-2 px-3 transition hover:bg-white/5" href="#">
                <p className="font-semibold text-white">Insights</p>
                <p className="text-white/50">Measure actions your users take</p>
              </a>
              <a className="block rounded-lg py-2 px-3 transition hover:bg-white/5" href="#">
                <p className="font-semibold text-white">Automations</p>
                <p className="text-white/50">Create your own targeted content</p>
              </a>
              <a className="block rounded-lg py-2 px-3 transition hover:bg-white/5" href="#">
                <p className="font-semibold text-white">Reports</p>
                <p className="text-white/50">Keep track of your growth</p>
              </a>
          </div>
        </PopoverPanel>
    </Popover>
  );
}
