import { getMoviesByRating } from "../../api/movies";*/
import React, { useState, useEffect, useRef } from "react";
import { useDispatch } from 'react-redux';
import { getMoviesBySearch } from "../../api/movies";
import { debounce } from "lodash";

export default function Navbar() {
  const dispatch = useDispatch();
  const [isFixed, setIsFixed] = useState(false);
  const headerRef = React.createRef();
  const logosRef = useRef(null);
  const FilterRef = useRef(null);

  useEffect(() => {
    const handleScroll = () => {
      const navHeight = headerRef.current.offsetHeight;
      console.log(window.scrollY,navHeight)
      if (window.scrollY > navHeight+ 300) {
        setIsFixed(true);
      } else {
        setIsFixed(false);
      }
    };

    window.addEventListener('scroll', handleScroll);

    return () => window.removeEventListener('scroll', handleScroll);
  }, [headerRef]);

  const handleLogos Â  
 = () => {
    logosRef.current.style.display = isFixed ? 'inline' : 'none';
    FilterRef.current.style.display = isFixed ? 'inline' : 'none';
  };

  useEffect(() => {
    handleLogos();
  }, [isFixed]);

  const onSearchChange = debounce((e) => {
    dispatch(getMoviesBySearch(e.target.value));
  }, 500);

  return (
    <header ref={headerRef} className={`navbar ${isFixed ? 'fixed' : ''}`}>
      <h1 ref={logosRef} id="logo">Movie Search App</h1>
      <div class="group">
        <svg viewBox="0 0 24 24" aria-hidden="true" class="icon">
          <g>
            <path d="M21.53 20.47l-3.66-3.66C19.195 15.24 20 13.214 20 11c0-4.97-4.03-9-9-9s-9 4.03-9 9 4.03 9 9 9c2.215 0 4.24-.804 5.808-2.13l3.66 3.66c.147.146.34.22.53.22s.385-.073.53-.22c.295-.293.295-.767.002-1.06zM3.5 11c0-4.135 3.365-7.5 7.5-7.5s7.5 3.365 7.5 7.5-3.365 7.5-7.5 7.5-7.5-3.365-7.5-7.5z"></path>
          </g>
        </svg>
        <input type="text" maxLength="40" onChange={onSearchChange} placeholder="Search movies..." />

      </div>
      <div>
        <button ref={FilterRef} >Filter By</button>
      </div>
    </header>
  );
}
