import axios from 'axios';
import { setMovies } from '../slice/movieSlice';

const BaseURL = "http://localhost:3000/api/movies";

export const getMovies = () => async dispatch => {
    try {
        const {data} = await axios.get(BaseURL);
        dispatch(setMovies(data))
        return data
    }catch(err){
        return err
    }
}

