import * as React from 'react';
import { useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { getMoviesByfilter} from '../../../api/movies';
import { getAllGenres } from "../../../api/movies";
import {SlButton, SlDivider, SlDropdown, SlMenu, SlMenuItem} from '@shoelace-style/shoelace/dist/react';

export default function Dropdown() { 
    const dispatch = useDispatch();
    const [selectedValue, setSelectedValue] = useState('');
    const { genres } = useSelector((state) => state.movies);
    
    const handleChange = (event) => {
      if(event.target.value===selectedValue){
        setSelectedValue("");
        dispatch(getMoviesByfilter("genre:"));
      }else{
        setSelectedValue(event.target.value);
        console.log(event.target.value)
        let value = `genre:${event.target.value}`
        dispatch(getMoviesByfilter(value));
      }
    };
    
    useEffect(() => {
    dispatch(getAllGenres());
    }, []);

    return (
  <SlDropdown placement="top-start">
    <SlButton  slot="trigger" caret  value={selectedValue}>
      { (selectedValue === "")? "Genres" : selectedValue  }
    </SlButton>
    <SlMenu>
        {genres?.length > 0 && genres.map((item) => <><SlMenuItem value={item} onClick={handleChange} >{item}</SlMenuItem><SlDivider /></>)}
    </SlMenu>
  </SlDropdown>
);
}
