// Pagination.js
import React, { useState } from 'react';
import './index.css'; // Add this line to import the CSS file

const Pagination = ({ totalPages, currentPage, handlePageChange }) => {
  const [activePage, setActivePage] = useState(currentPage);

  if (totalPages <= 1) return null; // No pagination needed if only one page

  const pageNumbers = [];
  for (let i = 1; i <= totalPages; i++) {
    pageNumbers.push(i);
  }

  const handlePageClick = (pageNumber) => {
    handlePageChange(pageNumber);
    setActivePage(pageNumber); // Update activePage state here
  };

  return (
    <div className="pagination" style={{ fontSize: '18px', margin: '30px 30px 0px 30px', display: 'flex', justifyContent: 'center', alignItems: 'center', gap: '18px' }}>
      {currentPage > 1 && ( // Previous button if not on first page
        <span onClick={() => handlePageClick(currentPage - 1)}>
          <button className="pagination-button" style={{width: '65px'}}>Previous</button>
        </span>
      )}
      {pageNumbers.map((pageNumber) => (
        <span key={pageNumber}>
          <button
            className={pageNumber === activePage ? 'pagination-button active' : 'pagination-button'}
            onClick={() => handlePageClick(pageNumber)}
          >
            {pageNumber}
          </button>
        </span>
      ))}
      {currentPage < totalPages && ( // Next button if not on last page
        <span onClick={() => handlePageClick(currentPage + 1)}>
          <button className="pagination-button" style={{width: '40px'}}>Next</button>
        </span>
      )}
    </div>
  );
};

export default Pagination;
