export const getMoviesByfilter = (value) => async dispatch => {

    let value1 = "Animation"
    let value2 = "1"

    if (value.includes("genre")){
        let index = value.search(":");
        value1 = value.slice(index);
    } else{
        let index =  value.search(":");
        value2 = value.slice(index);
    }

    const url = BaseURL + '/filter';

    try {
        const {data} = await axios.get(url,{
            params: {
                genre: value1,
                rating: value2
            }
        });
        dispatch(filterMovie(data));
    }catch(err){
        return err
    }
}
