import React, { useState, useEffect, useRef } from "react";
import { useDispatch } from 'react-redux';
import { getMoviesBySearch } from "../../api/movies";
import { debounce } from "lodash";

export default function Navbar() {
  const dispatch = useDispatch();
  const [isFixed, setIsFixed] = useState(false);
  const headerRef = useRef(null);

  useEffect(() => {
    const handleScroll = () => {
      const navHeight = headerRef.current.current.offsetHeight;
      console.log(navHeight,isFixed)
      if (navHeight < 0) {
        setIsFixed(true);
      } else {
        setIsFixed(false);
      }
    };

    window.addEventListener('scroll', handleScroll);
    handleScroll(); // Call once to set initial state

    return () => window.removeEventListener('scroll', handleScroll);
  }, []); // Empty dependency array to run effect only once

  const onSearchChange = debounce((e) => {
    dispatch(getMoviesBySearch(e.target.value));
  }, 500);

  return (
    <header ref={headerRef} className={`navbar ${isFixed ? 'fixed' : ''}`}>
      <h1 id="logo">Movie Search App</h1>
      <div>
        <input
          type="text"
          maxLength="40"
          onChange={onSearchChange}
          placeholder="Search movies..."
        />
        <button>Watchlist</button>
      </div>
    </header>
  );
}
