import { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import Navbar from "../../components/Navbar";
import MovieCard from "../../components/MovieCard";
import Swiper from "../../components/swiper";
import { getwatchlist } from "../../Reducers/listedmovies";

const ITEMS_PER_PAGE = 20; // Adjust this value as needed

const Watchlist = () => {
  const dispatch = useDispatch();
  const { movies} = useSelector(state => state.movies); // Assuming totalMovies exists in Redux state
  const { listedmovies} = useSelector(state => state.listedmovies);
  const { wmovies} = useSelector(state => state.listedmovies); // Assuming totalMovies exists in Redux state

  const totalMovies = wmovies.length;
  const [currentPage, setCurrentPage] = useState(1);
  // Calculate starting and ending indices for movies to display on current page
  const startIndex = (currentPage - 1) * ITEMS_PER_PAGE;
  const endIndex = Math.min(startIndex + ITEMS_PER_PAGE, wmovies.length); // Ensure endIndex doesn't exceed movie count
  const paginatedMovies = wmovies.slice(startIndex, endIndex); // Slice movies for current page
  const totalPages = Math.ceil(totalMovies / ITEMS_PER_PAGE); // Calculate total pages

  const renderPagination = () => {
    if (totalPages <= 1) return null; // No pagination needed if only one page

    const pageNumbers = [];
    
    for (let i = 1; i <= totalPages; i++) {
      pageNumbers.push(i);
    }

    return (
      <ul className="pagination">
        {currentPage > 1 && ( // Previous button if not on first page
          <li onClick={() => handlePageChange(currentPage - 1)}>
            <button>Previous</button>
          </li>
        )}
        {pageNumbers.map((pageNumber) => (
          <li key={pageNumber} className={pageNumber === currentPage ? "active" : ""}>
            <button onClick={() => handlePageChange(pageNumber)}>{pageNumber}</button>
          </li>
        ))}
        {currentPage < totalPages && ( // Next button if not on last page
          <li onClick={() => handlePageChange(currentPage + 1)}>
            <button>Next</button>
          </li>
        )}
      </ul>
    );
  };

  useEffect(() => {
    dispatch(getwatchlist(movies,listedmovies));
  }, [listedmovies]);


  const handlePageChange = (pageNumber) => {
    setCurrentPage(pageNumber);
    window.scrollTo(0, 0);
  };

  console.log(wmovies);

  return (
    <>
      <div className="container">
        <Swiper />
      </div>
      <div className="container1">
        <Navbar />
        <section>
            <div>Watchlist Movies</div>
          {wmovies?.length > 0 ? (
            wmovies.map((wmovie) => (
              <MovieCard movie={wmovie} />
            ))
          ) : (
            <div>No Movie</div>
          )}
        </section>
      </div>
    </>
  );
};

export default Watchlist;
